package com.Sel;

public class PrimeNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i;
		for(i=0;i<10;i++) 
		{
			if((i/2)==0) {
				System.out.println("Given number is  Prime" + i);
			}
				else {
					System.out.println("Is a prime number" + i);
				}
			}
		}

}
